INSERT INTO `comment` (`id`, `comment_type`, `posted_at`, `place_id`, `parent_comment_id`, `member_id`, `name`, `mail`, `title`, `comment`, `url`, `thumbnail`, `delete_flag`, `created_at`, `updated_at`) VALUES
(22, 1, '2013.01', 101, 0, NULL, 'edea.jp', '', 'SHOBU STYLE 鹿児島発、創造的福祉エンタテイメント', '彼らの感性はものすごい。何のコピーでもなく、まったく自由な創作表現なのです。マイナスの部分にばかり目を向け無理に指導するよりも、彼らのプラスの部分、自由な表現や創造性を輝かせたい。\r\n「障がい者が作ったものだから」といって売れるのでは意味がない。\r\n障害のある利用者の方々を「助けてあげる」という目線ではできない。もっと人間同士、対等な目線で向き合い、私たちから彼らに寄り添っていき、共に創り出すという意識で常にいます。\r\n', 'http://edea.jp/japanese/japan/kagoshima/kagoshima/kagoshima_p1.html', '/img/shobu/s1.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(23, 1, '2013.01', 101, 0, NULL, 'greenz.jp', '', '知的障がい者施設しょうぶ学園から生まれた音パフォーマンス集団「otto&orabu」', '他の施設は、彼らが”健常者”と言われるひとのように少しでも振る舞えるよう、僕が言うピュアな所を修正し訓練していくから元の味がなくなってしまう。その元の味っていうのは、彼ららしさだし、その人らしさじゃないですか。とにかくそこを否定したくなかった。\r\n僕たちはもっと楽であるべき。自分を否定しないでほしい。\r\n僕は、「あなたは、そのままでいいんですよ」と叫ぶ。', 'http://greenz.jp/2013/01/16/otto_orabu/', '/img/shobu/s2.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(24, 1, '2012', 101, 0, NULL, 'mammo.tv', '', '生きる上で本当に必要な知恵とは何か～しょうぶ学園の問いかけ', '彼らを見ていると、人として大事なことは、食べる・寝る・行為することじゃないか。それらで自分の満足や快適、安心を得ている。つまり本能を満たしている。それは知的な進化と発達で健常者から失われてきたことであり、一方で彼らは知的に発達していないがゆえに、守られた本能が浮き出て来る。\r\n君はそれでいい。いまの若い人はそう言われた経験があまりないんじゃないかな。僕は「変わらなくていいよ」と思っています。なぜなら、あなたの中にすでにもっているものがあるから。', 'http://www.mammo.tv/interview/archives/no309.html', '/img/shobu/s3.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(25, 1, '2012', 101, 0, NULL, 'nosvis.com', '', 'しょうぶ学園「Nui Projectシャツ」展', 'ただ、そこでもやっぱり、世の中の人たちに少しでも近づくと褒められて。でも、教えられて直されて作っている作り手も、スタッフも、苦しくなってしまったんですよ。\r\n普通ってなんだろう、当たり前って何だろうって考えたときに、誰がクレイジーなのかなんてわからないじゃないか、割合だけの話しじゃないかって思えてきたんです。\r\n', 'http://nosvis.com/news/nui-project/', '/img/shobu/s4.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(26, 1, '2009', 101, 0, NULL, 'Judd No.3', '', 'モノは美しい心から生まれる', '工房しょうぶのものづくりは、ものごとを解釈してつくるという知恵のついた大人の感じ方と子どもの新鮮な印象を感じてつくる違いのようなものを融合させながら進めています。知恵の発達が美しさの解釈や表現を鈍感にさせ邪魔するということを知りながら、対極的な彼らの行為の貴重性を考えています。\r\n', 'http://www.nijidoku.com/db/090520001/index.html#page=31', '/img/shobu/w1.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(27, 1, '2010', 101, 0, NULL, 'Judd No.5', '', '共鳴する「不揃いな音」', '「はたして揃うことがすべて美しいことだろうか」と問いかけてみると、見えている世界には、実は見えていない別の可能性があることにも気づく。見方を変えれば、揃わない面白さは、それぞれの人の違いや中身の違いを個性や躍動として全面に押し出し刺激的に見せつけるところにある。', 'http://www.nijidoku.com/db/100730001/index.html#page=19', '/img/shobu/w2.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(28, 3, '2003.10', 101, 0, NULL, '小出由紀子', '', '展覧会 Fabulous Fabrics に寄せて', '菖蒲学園では、これらの着ることのできないシャツを「アートワーク」として敬意を払っている。知的障害者のためのクラフト工房から、アートが生まれつつある。世界でも例を見ない、ここは理想郷だ。', 'http://www.yukikokoide.com/', '/img/shobu/book1.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(29, 3, '2010.04', 101, 0, NULL, 'ナガオカケンメイ', '', '障がい者施設を変える。しょうぶ学園の挑戦。', '気持ちをそのまま表している彼らを見ていると、ただ、なんとも言えない気持ちになる。たぶん、それはうらやましくもあり、微笑ましくもあり、そして、残念ながら健常者からすれば特別なことなのだ。', 'http://www.amazon.co.jp/design-travel-KAGOSHIMA-DEPARTMENT-PROJECT/dp/4903097021/', '/img/shobu/r1.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(30, 3, '2013.09', 101, 0, NULL, 'Gomi Fumiko', '', 'しょうぶ学園という社会のありかた', '「彼ら（利用者）は、結果ではなく、瞬間の行為に幸福を感じている。社会的価値や賞賛に無関心である。」\r\nこれこそ　私たちが生きる社会に必要な理念なのではないか？\r\n嫌だなと　感じることがあるならば、それをやめて、自分がいいなと感じる暮らしをしなくてはならない。感覚にうそをついて、いったい何を作るのか、たぶん原発のある社会をつくるんだ。', 'http://ameblo.jp/5323ko/entry-11605706491.html', '/img/shobu/b1.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(31, 3, '2013.07', 101, 0, NULL, 'Atelier Manis', '', '７月の日本 その４ しょうぶ学園２', 'もしかしたら、特別なことではなくて、人間みな生まれながらにこうした固有の表現力を持っているのかな、って。\r\n', 'http://ateliermanis.air-nifty.com/blog/2013/07/post.html', '/img/shobu/b2.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(32, 3, '2011.03', 101, 0, NULL, 'k-work.net', '', 'SHOBU', 'こうすれば褒められるだろう？こんな感じが売れ筋だろう？どんなアーティストにも必ずあるジレンマが彼らには無い、刺繍を好きな人は単純に針を刺す事が好きで、感性の赴くままに糸を選び針を刺す。\r\n', 'http://blog.k-work.net/?eid=1269494', '/img/shobu/b4.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(33, 3, '2011.10', 101, 0, NULL, 'yokoyamazaki', '', 'しょうぶ学園', 'あのすごい刺繍のシャツが生まれてくる現場は、やはりすごい所だった。\r\n人が本来持っている力を、社会の中でどのように伸ばして表現していけば良いのか。今言われているアートとは何なのか。人が心地よく生きるとはどういうことなのか。\r\n出来ることからはじめるというシンプルなこと、何にも規制を受けない表現を無理なく支援するということから、饒舌な世界が生まれてくるようなしょうぶ学園の試みを見ると、今の社会は余計なことが多くなり過ぎているように思えて来た。', 'http://d.hatena.ne.jp/yokoyamazaki/20111006/1317849654', '/img/shobu/b3.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(34, 3, '2013.06', 101, 0, NULL, 'daikatoti', '', 'しょうぶ学園のコンサートと田口ランディの講演に行く。', '江戸時代以前の管理される前の日本は、もしかしたらああいう自由な民族だったんじゃないだろうかと思った。\r\nこういうところにも自分が思い込みというものに縛られているなと感じたし、なにかもっと違う世界があるんだよなと気付かせてくれたかな。\r\n', 'http://daikatoti.exblog.jp/18930605/', '/img/shobu/b5.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(35, 3, '2012.05', 101, 0, NULL, '愚樵', '', '魂に「蓋」をするもの　その１', '彼らが奏でたのは「自然の音」だったと思う。たとえば、鳥や虫たちの鳴き声のような。\r\n', 'http://gushou.blog51.fc2.com/blog-entry-677.html', '/img/shobu/b6.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(36, 3, '2013.08', 101, 0, NULL, '尹雄大', '', '【自叙帖 100％コークス】vol.9', 'しょうぶ学園は世間的には「障害者支援施設」という衣を被っているけれど、それは偽装で静かに革命を行なっている拠点だ。ビルを爆破することも自動車に爆弾を仕掛けることもないし、本屋で檸檬を置くようなこともないけれど、物質には影響を及ぼさない爆風が人の心を吹き抜け、何かを変える。そういう力があそこには宿っている。\r\n', 'http://nonsavoir.com/blog/?p=1664', '/img/shobu/b7.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(37, 3, '2012.08', 101, 0, NULL, 'モリヤマコウジ', '', '７月の日本 その４ しょうぶ学園２', '毎日の生活を生きやすく生きるために手に 入れた「形式的な解釈」や「社会機能」によって抑え込まれていたエネルギーが呼び覚まされるのだ。障害を持つ作家たちに制作の意図や目的はない。競争心もない。欲もな い。ただ、内から突き動かされる衝動「Libido」のまま、行為そのものに喜びを感じ、 作り上げているのだから。', 'http://kojimoriyama.net/blog/620', '/img/shobu/b9.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(38, 3, '2012.04', 101, 0, NULL, 'fukumoriso', '', 'twitter', 'アウトサイダーアートでもアールブリュットでもないんだよなーきっと。ただ自然な事なんだよな。水とか石とか木とか風とかみたいなもんなんだよなきっと。アートに救われてアートが邪魔をしてる感じ。', 'http://twitter.com/fukumoriso/status/195662599487094784', '/img/shobu/b13.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(39, 3, '2013.08', 101, 0, NULL, 'NPO法人 essence', '', '鹿児島・しょうぶ学園さんに行ってきました', 'ふと口にされた「ノーマライゼーションって言いますが、そもそもノーマルって何なんでしょう」と言う言葉に、はっとする思いでした。\r\nその人その人の本質に寄り添う、その人その人が持つ力を尊重する、そうすることが、歪みのない社会を創るのでは…と感じました。', 'http://npoessence.exblog.jp/18354555/', '/img/shobu/b8.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(40, 3, '2010.10', 101, 0, NULL, '月と波', '', '鹿児島・しょうぶ学園へ', '本当の豊さって何？学ぶべきはむしろ、健常者と言われる側の方かもしれない、などと感じた時間でした。', 'http://blogs.yahoo.co.jp/tukitonami0720/19314291.html', '/img/shobu/b12.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(43, 3, '2012.06', 101, 0, NULL, 'つぶやきホルン', '', '「しょうぶ学園」の記事', '「障害者たちは、邪念を計算に入れず、自分がやりたい表現を純粋に出せる。私たちこそ、周りに縛られて自分の表現を押える障害があるのではないか。」\r\n\r\n「私たちの価値観から彼らを障害者と言っているが、本当に私たちが「健常者」で、彼らが「障害者」なのか考えなければならない。」\r\n', 'http://horn.yoka-yoka.jp/e859792.html', '/img/shobu/b10.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(44, 1, '2008', 101, 0, NULL, 'Judd No.2', '', '「場」中庭のある風景', '幸福な感性に満たされ、クリエイティブで刺激的なコミュニティーを創るためには、自然環境を見直し、生きていくために必要なことを守っていくことが大切だと考えています。そして、人間と環境の持続可能なインターフェイス（媒介）として存在し、この場から新しいコミュニケーションが生まれ、障害のあるなしに関係なく人々が幸福に繋がっていくことを夢見ています。', 'http://www.nijidoku.com/db/081202006/index.html#page=15', '/img/shobu/w3.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(45, 3, '2013', 101, 0, NULL, '尹雄大', '', '爆発する創意　しょうぶ学園の挑戦', '人間の魅力とは生命の魅力である。私たちは社会によって生み出されたのではなく、社会の外からやって来た。生きることの意味は社会的な価値に還元されることはない。しょうぶ学園の試みは、私たちが直視することを避けている「人の能力とは？」「生きるとは何か？」を鋭く問うている。', 'http://imawoikiruhito.net/wp-content/uploads/2013/07/kotoba.pdf', '/img/shobu/s5.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02'),
(46, 1, '2013', 101, 0, NULL, 'kotoba', '', '爆発する創意 しょうぶ学園の挑戦', '健常者がノーマルではないかもしれないのに、それに障害者を合わせていたら、ノーマライゼーションは僕らの当り前を彼らに合わせることでしかない。', 'http://imawoikiruhito.net/wp-content/uploads/2013/07/kotoba.pdf', '/img/shobu/s5.png', 0, '2013-09-21 14:21:02', '2013-09-21 14:21:02');
