INSERT INTO `event` (`id`, `place_id`, `name`, `startDate`, `endDate`, `year`, `closeddays`, `openhours`, `place`, `price`, `contents`, `image`, `url`, `contact`, `latitude`, `longitude`, `delete_flag`, `created_at`, `updated_at`) VALUES
(1, 101, '真夏のTシャツとうちわ展', '2013-07-30', '2013-08-30', '', '月曜', '10：00 〜 17：00', '鹿児島　しょうぶ学園内Sギャラリー', '無料', '日程：2013年7月30日（火）〜8月30日（金）\n時間：10：00 〜 17：00\n休館：月曜\n場所：しょうぶ学園内Sギャラリー\n入場：無料\n\n色と文字が騒ぐTシャツ。点と線が揺れるうちわ。\n工房しょうぶのメンバーがつくり出す、かわいいけどちょっとおかしな、シンプルだけど何だか不思議な世界がTシャツとうちわに描かれました。\n暑い夏に良く似合うしょうぶアイテムをお探しに、\n是非お誘い合わせの上ご来場下さい。', 'http://www.shobu.jp/images/news/t-shirts2013s.jpg', '', '', NULL, NULL, 0, '2013-09-21 15:36:14', '0000-00-00 00:00:00'),
(2, 101, 'Good neighbors jamboree 2013', '2013-08-31', NULL, '', '', '11：00 〜', '鹿児島　かわなべ森の学校', '¥4,500', '日程：8月31日（土）11：00 〜\n場所：かわなべ森の学校\n前売りチケット：¥4,500\n\n今年も Good Neighbors Jamboree の季節がやってまいりました！\n森の学校を舞台に音楽、クラフト、デザイン、アートなどなど\n様々なジャンルが集い楽しむ、参加型フェスティバルです。\nしょうぶ学園は今年もパーカショングループ otto & orabu で盛り上げたいと思います！\n', 'http://www.shobu.jp/images/news/gnj2013.jpg', 'http://goodneighborsjamboree.com/', '', 31.344, 130.38, 0, '2013-09-22 19:46:07', '0000-00-00 00:00:00'),
(3, 101, 'Less is More 「しょうぶ学園の手仕事」展', '2013-06-12', '2013-06-26', '', '会期中無休', '最終日は17時まで', '東京　LOGOS GALLERY', '無料', '会場：LOGOS GALLERY　ロゴスギャラリー\r\n渋谷パルコ　パート1 / B1F\r\n期間：2013/06/12 (水) －2013/06/26 (水) \r\n最終日は17時まで\r\n入場料：無料\r\n\r\n「無垢な心だからこそ生まれる自在な美しさ」\r\n\r\n鹿児島の吉野町にある「しょうぶ学園」は、\r\n知的障がい者が地域社会で暮らしていくための様々なことを\r\n支援するための施設です。\r\n\r\nここでは、障がいを持つ方が無心に縫った布、\r\n削り取った木材などをデザインの中心にしながら編集することで\r\n魅了ある商品を生み出しています。\r\n', 'http://www.parco-art.com/images/w660/lessisimoredm.jpg', 'http://www.parco-art.com/web/logos/exhibition.php?id=563', '03-3496-1287', 35.6622, 139.699, 0, '2013-09-22 19:46:17', '0000-00-00 00:00:00'),
(4, 101, 'SHOBU STYLE 〜美しい行為からうまれるカタチ〜', '2013-07-07', '2013-07-28', '', '7月17日（水）', '9：00 〜 19：00', '奄美　田中一村記念美術館企画展示室', '無料', 'オープニング記念芸術文化講演\r\n日時：7月7日（日）13：30 〜 15：30\r\n場所：田中一村記念美術館ガイダンス室・企画展示室\r\n講師：福森伸（しょうぶ学園統括施設長）\r\n演題：「工房しょうぶの美意識エンパワメント」\r\n', 'http://www.shobu.jp/images/news/syoubugakuenn.JPG', 'http://www.amamipark.com/isson/isson.html', '', 28.418, 129.694, 0, '2013-09-22 19:46:50', '0000-00-00 00:00:00'),
(5, 101, '今を生きる人の集い', '2013-08-23', '2013-08-25', '', '', NULL, '滋賀　湖邸滋びわこクラブ', '', '日程：8月23日（金）〜8月25日（日）\r\n会場：湖邸滋びわこクラブ\r\n講師陣：関根秀樹、甲野善紀、光岡英稔、名越康文、北川貴英、嘉陽与南、福森伸\r\n\r\n内容：〜今を生きる人のつどい HPより抜粋〜\r\n“今の今を生きる” をキーワードに企画された合宿形式の研修です。\r\n前回は共に集った講師陣と参加者が、互いへの交流を通じて個々の中にある新たな方向性を見い出し自らの生き方を省みていく、そのような場として開催し成功を遂げました。\r\n\r\nしょうぶ学園からは施設長、福森伸が講師として参加いたします。\r\n', 'http://imawoikiruhito.net/wp-content/uploads/2013/03/icon200x200.jpg', 'http://imawoikiruhito.net/tsudoi/2013_08/2013-08_outline/', '', 35.2643, 135.991, 0, '2013-09-22 19:47:02', '0000-00-00 00:00:00'),
(6, 100, 'べてるまつり２０１３ ', '2013-08-10', NULL, '', '', '10:00~', '浦河町総合文化会館', '一部・二部 ３５００円', '10:00~　べてるまつり第一部 浦河町総合文化会館　文化ホール\r\n\r\n14:30~　べてるまつり第二部\r\nうらかわ優駿ビレッジAERU\r\n※会場までのバスの送迎があります。文化会館前から出ます。帰りの送迎もあります。\r\n野外での開催となりますので、北海道の夏は涼しいとはいえ、日光・暑さ対策、虫対策、悪天候対策などをお願いします。\r\n\r\n17:00~　閉会\r\n\r\n参加費：一部・二部 ３５００円\r\n\r\n夜　後夜祭\r\n場所 うらかわ優駿ビレッジAERU\r\n参加費：３５００円\r\n\r\n※ウレシパの会総会が１０日１２時１５分よりAERUであります。\r\n参加費：１５００円（お弁当付き）\r\n\r\nお申し込み\r\n参加のお申し込みは下記のPDFファイルをダウンロードして、必要事項を記入の上、べてるの家までお送りください。\r\n\r\n● べてるまつり２０１３要項（PDF）', '', 'http://bethel-net.jp/?p=484', '', NULL, NULL, 0, '2013-09-21 22:19:36', '0000-00-00 00:00:00'),
(7, 101, 'しょうぶ学園展 細胞の記憶 〜表現のかたち〜', '2013-09-26', '2013-10-12', '', '月曜', NULL, '京都　京都造形大学ギャルリ・オーブ', '無料', '日程：2013年9月26日（木）〜10月12日（土）\n時間：10：30 〜 18：30\n※最終日10月12日（土）は17：00まで\n場所：京都造形大学ギャルリ・オーブ［人間館1F］\n【ギャラリートーク】\n日時：9月27日（金）17：00〜18：00\n※終了後ささやかなレセプションを行います。\nゲスト講師：福森伸\n（しょうぶ学園施設長/工房しょうぶ主宰/社会福祉士）\n\n以下フライヤーより抜粋\nしょうぶ学園では、障がいを持つ人たちの感性あふれる創作の姿勢に魅せられ、芸術・音楽活動を中心に個性的な活動を行っています。工房を利用する人だけでなく、サポートに携わるスタッフも表現者という同じ立場でコラボレーションをしながら、「与えられる側」から「創りだせる側」に立つことによって、障害者、健常者の枠を取り払った対等な関係づくりを目指しています。\nそうした環境から生まれた作品は、クラフトやアートの世界から高く評価され、\n開催（出展）する展覧会は国内だけでなく、海外にもおよんでいます。', 'http://www.shobu.jp/images/news/saibounokioku.jpeg', 'http://aube.kyoto-art.ac.jp/archives/1207', '', 35.0368, 135.792, 0, '2013-09-21 22:19:27', '0000-00-00 00:00:00'),
(8, 101, 'FREE POP EXPO', '2013-09-13', '2013-10-08', '', '月曜', NULL, '大阪　梅田ロフト', '無料', '日程：2013年9月13日（金）〜10月8日（火）\n時間：10：30 〜 21：00\n※最終日17：00時閉場\n場所：梅田ロフト 7Fロフトフォーラム\n【トークイベント】\n日時：9月28日（土）14：00〜15：30\n出演：福森伸（工房しょうぶ/しょうぶ学園）\n聞き手：松岡由希子（フリーライター）\n\n以下フライヤーより抜粋\n障がいを持つ”つくり手”が、土・木・布・紙などの素材から創作した「かわいい！」「おもしろい！」「ステキ！」な、フリポップ雑貨たちを、工房での知られざる”ものづくり”の現場の様子とともにお届けする、\nユニークな博覧会です。\n工房しょうぶからはクラフトが展示販売されます。', 'http://www.shobu.jp/images/news/umeda_loft.jpeg', 'http://www.loft.co.jp/shoplist/umeda', '', 34.708, 135.5, 0, '2013-09-26 12:08:47', '0000-00-00 00:00:00'),
